import matplotlib.pyplot as plt  #Import de la bibliothèque pour tracer les courbes))f=open("../Utiles/PL.txt","r")   #Ouverture du fichier))LX,LY=[],[]                      #Initialisation des listes qui vont contenir le tracé du trajet du robot))DonneeTemp=[]                    #Liste temporaire pour la lecture du fichier))for ligne in f:                  #Pour chaque ligne du fichier)    DonneeTemp=ligne.split(";")  #On sépare les abscisses et les ordonnées dans la liste temporaire)    LX.append(float(DonneeTemp[0])) #Puis on les copie dans les bonnes listes)    LY.append(float(DonneeTemp[1])))))L2X=[0, 17.71, 35.42]            #On place les coordonnées de la pyramide)L2Y=[0, 30.67, 0])plt.plot(LX,LY)                  #On trace le trajet)plt.plot(L2X,L2Y, "red")         #On trace le contour de la pyramide)plt.axis('equal'))plt.show()